const root = am5.Root.new("chartdiv");

// Set themes
// https://www.amcharts.com/docs/v5/concepts/themes/
root.setThemes([
	am5themes_Animated.new(root)
]);


// Create chart
const chart = root.container.children.push(am5timeline.CurveChart.new(root, {
	wheelY: "zoomX"
}));

const scrollbarX = chart.set("scrollbarX", am5.Scrollbar.new(root, {
	orientation: "vertical",
	height: am5.percent(25),
	position: "absolute",
	x: am5.percent(58),
	centerY: am5.p50
}));

scrollbarX.adapters.add("y", function(y) {
	return chart.curveContainer.y();
})

chart.plotContainer.children.push(scrollbarX);


const yRenderer = am5timeline.AxisRendererCurveY.new(root, {
	axisLocation: -0.1
})

yRenderer.labels.template.setAll({
	forceHidden: true
});

yRenderer.grid.template.set("forceHidden", true);

// Create axes and their renderers
const xRenderer = am5timeline.AxisRendererCurveX.new(root, {
	points: getPoints(),
	yRenderer: yRenderer,
	strokeDasharray: [2, 3],
	strokeOpacity: 0.5,
	stroke: am5.color(0x000000)
});

xRenderer.labels.template.setAll({
	centerY: am5.p50,
	fontSize: 11,
	minPosition: 0.01
});

xRenderer.labels.template.setup = function(target) {
	target.set("layer", 30);
	target.set("background", am5.Rectangle.new(root, {
		fill: am5.color(0xffffff),
		fillOpacity: 1
	}));
}

const yAxis = chart.yAxes.push(am5xy.CategoryAxis.new(root, {
	maxDeviation: 0,
	categoryField: "category",
	renderer: yRenderer
}));

const xAxis = chart.xAxes.push(am5xy.DateAxis.new(root, {
	baseInterval: { timeUnit: "minute", count: 1 },
	renderer: xRenderer,
	tooltip: am5.Tooltip.new(root, {})
}));

// Data
const colorSet = chart.get("colors");

const series = chart.series.push(am5timeline.CurveColumnSeries.new(root, {
	xAxis: xAxis,
	yAxis: yAxis,
	baseAxis: yAxis,
	categoryYField: "category",
	valueXField: "end",
	openValueXField: "start",
	maskBullets: false
}));

series.columns.template.setAll({
	height: am5.percent(20),
	templateField: "settings",
	strokeOpacity: 0
})

series.bullets.push(function(root, series, dataItem) {
	const container = am5.Container.new(root, {
		centerY: am5.p100
	});

	container.children.push(am5.PointedRectangle.new(root, {
		cornerRadius: 5,
		width: 32,
		height: 32,
		pointerBaseWidth: 16,
		centerY: am5.p100,
		centerX: am5.p50,
		templateField: "settings",
		pointerX: 16,
		pointerY: 40,
		strokeOpacity: 0,
		shadowColor: am5.color(0x000000),
		shadowBlur: 5,
		shadowOffsetX: 2,
		shadowOffsetY: 2,
		tooltipText: dataItem.dataContext.text,
		tooltipY: 0
	}))

	container.children.push(am5.Picture.new(root, {
		centerY: 32,
		centerX: am5.p50,
		width: 20,
		height: 20,
		src: dataItem.dataContext.icon
	}));

	return am5.Bullet.new(root, {
		sprite: container
	});
})

